-- move data from the release_issues table to the monthly_issues
INSERT INTO monthly_issues (guid, id, type, category, status, prio, name, description, owner, update_time) SELECT release_issues.guid, release_issues.id, release_issues.type, release_issues.category, release_issues.status, release_issues.prio, release_issues.name, release_issues.description, release_issues.owner, release_issues.update_time FROM release_issues  WHERE 1=1 and type = 'task' ON CONFLICT (id) DO UPDATE SET guid = excluded.guid ,id = excluded.id, prio = excluded.prio,status = excluded.status ,category = excluded.category ,name = excluded.name ,description = excluded.description ,type = excluded.type ,owner = excluded.owner , update_time = excluded.update_time


